mapper.select:
select a.* from ( select b.*, rownum rn from (select m.member_id, m.member_email, l.list_date from members m , black_list l where m.member_id = l.member_id
AND m.member_id LIKE '%' || #{searchKeyword} || '%'
order by l.list_date desc) b) a where rn between #{first} and #{last}

select a.* from ( select b.*, rownum rn from (select * from members m where not exists( select member_id from black_list l where l.member_id = m.member_id)
AND m.member_id LIKE '%' || #{searchKeyword} || '%'
) b) a where rn between #{first} and #{last}
