mapper.insert:
    INSERT INTO complain(
    					 	complain_no,
    					 	complain_type,
							complain_title,
							complain_content,
							member_id,
							board_no
									)
				VALUES (
					complain_seq.nextval
					,#{complain_type} 
					,#{complain_title}
					,#{complain_content}
					,#{member_id}
					,#{board_no}
					)

mapper.select:
 
  select a.* from ( select b.*, rownum rn from ( 
 				
 				select * 
				from complain
				where complain_type = #{complain_type}
		
          	
				and member_id = #{member_id}
			
				
				order by complain_date desc
				
  ) b) a where rn between #{first} and #{last}

SELECT count(*)
FROM complain
where complain_type = #{complain_type}

mapper.update:
update complain 
set complain_check = #{complain_check}
where complain_no = #{complain_no}

		and member_id = #{member_id}

mapper.insert:
    INSERT INTO message(
    					 	message_no,
    					 	sender_id,
							message_title,
							message_content,
							receiver_id
									)
				VALUES (
					complain_seq.nextval 
					,'admin' 
					,#{message_title}
					,#{message_content}
					,#{receiver_id}
					)
