mapper.select:
select a.* from ( select b.*, rownum rn from (select m.member_id , sum(a.acorn_charge) as acron, g.grade_kor , m.upload_count from acorn a join members m on a.member_id = m.member_id join grade g on m.member_grade = g.member_grade
g.member_grade=#{member_grade}
group by m.member_id, g.grade_kor, m.upload_count order by grade_kor asc) b) a where rn between #{first} and #{last}

select count(distinct m.member_id) from acorn a join members m on a.member_id = m.member_id join grade g on m.member_grade = g.member_grade
g.member_grade=#{member_grade}
order by grade_kor asc

mapper.update:
update  members
   set	MEMBER_GRADE = #{member_grade}
 where  member_id = #{member_id}
